if (!_spinner)    {        _spinner = [[UIActivityIndicatorView alloc] initWithActivityIndicatorStyle:UIActivityIndicatorViewStyleGray];        _spinner.hidesWhenStopped = YES;        _spinner.hidden = YES;    }    return _spinner;}#pragma mark - Private Utils- (void)setupLayouts{    [self.imageView mas_makeConstraints:^(MASConstraintMaker *make)    {        make.edges.equalTo(self.view);    }];    void (^topElementMaker)(MASConstraintMaker *) = ^(MASConstraintMaker *make)    {        make.centerX.equalTo(self.view);        make.top.equalTo(self.view).with.offset(20);    };    [self.spinner mas_makeConstraints:topElementMaker];    [self.errorLabel mas_makeConstraints:topElementMaker];}@end